import Navbar from "../../components/Navbar/Navbar"
import styled from "../CreateArticle/CreateArticle.module.css"
import React, { useState } from "react";



function CreateArticle() {

    const [articleTitle, setArticleTitle] = useState("")

    const handleChangeArticle = (e) =>{
        setArticleTitle(e.target.value);
    };

    return (

        <>
            <Navbar title="مهدیس بلاگ" />
            <div className={styled.CreateArticlePage}>
                <div className="container">
                    <h1> ساخت مقاله </h1>

                    <div className={styled.inputWrapper}>
                        <label> عنوان </label>
                        <input name="title" onChange={handleChangeArticle} type="text" />
                    </div>
                    <div className={styled.inputWrapper}>
                        <label> تاریخ </label>
                        <input onChange={handleChangeArticle} type="text" />
                    </div>
                </div>

            </div>

        </>

    )
}


export default CreateArticle
