import { useEffect, useState } from "react";
import Article from "../../components/Article/Article";
import Navbar from "../../components/Navbar/Navbar";
import styled from "../home/Home.module.css"

function Home() {

    const [articles, setArticles] = useState([]);

    useEffect(() => {
        setArticles([
            { id: 1, imageUrl: "", title: "عنوان اول", readingTime: 5 },
            { id: 2, imageUrl: "", title: "عنوان دوم", readingTime: 3 },
            { id: 3, imageUrl: "", title: "عنوان سوم", readingTime: 3 }
        ]);
    }, []); // این [] مهمه که فقط یکبار useEffect اجرا بشه



    // let articles = [

    return (
        <div className={styled.homeWrapper}>
            <Navbar title="مهدیس بلاگ" />
            <div className="container">
                <h2>مقالات جدید</h2>

                <div className={styled.articleList}>
                    <div className={styled.articles}>
                        {
                            articles.map(article => (
                                <Article key={article.id} article={article} />
                            ))
                        }
                    </div>






                </div>

            </div>
        </div>

    )
}

export default Home
