import Navbar from "../../components/Navbar/Navbar"
import styled from "../CreateArticle/CreateArticle.module.css"
import React, { useState } from "react";



function CreateArticle() {

    const [article, setArticle] = useState({
        title: "",
        date: "",
        athor: "",
        readingTime: ""
    }
    )

    const handleChangeArticle = (e) => {
        setArticle((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value
        }))
    };
    console.log(article);

    return (

        <>
            <Navbar title="مهدیس بلاگ" />
            <div className={styled.CreateArticlePage}>
                <div className="container">
                    <h1> ساخت مقاله </h1>

                    <input label="عنوان" name="title" handleChange={handleChangeArticle} />
                    <input label="تاریخ" name="title" handleChange={handleChangeArticle}/>
                    <input label="مدت زمان خواندن" name="title" handleChange={handleChangeArticle}/>
                    <input label="نویسنده" name="title" handleChange={handleChangeArticle}/>

                </div>

            </div>

        </>

    )
}


export default CreateArticle
