import styled from "./TextArea.module.css"

function TextArea(props) {
    return (
        <div className={styled.textareaWrapper}>
            <label> {props.label}</label>
            <textarea> onChange={props.handleChange} </textarea>
        </div>
    )
}


export default TextArea
